    public static int longestStrChain(String[] words) {

        Arrays.sort(words, (a, b) -> a.length() - b.length());

        var dp = new int[words.length];
        System.out.println(Arrays.toString(words));

        Arrays.fill(dp, 1);
        int best = 1;
        for (int i = 1; i < words.length; i++) {
            var cl = words[i].length();
            int j = i - 1;
            int max = 0;
            while(j >= 0 && words[j].length() == cl) {
                j--;
            }
            while (j >= 0 && words[j].length() == cl - 1) {
                if (doesChain(words[i], words[j])) {
                    dp[i] = Math.max(dp[i], dp[j] + 1);
                    best = Math.max(dp[i], best);
                }
                j--;
            }
        }
//        System.out.println(Arrays.toString(dp));


        return best;

    }

    static boolean doesChain(String big, String small) {
        int p2 = 0;
        for (int x = 0; x < small.length(); x++) {
            if (big.charAt(p2) != small.charAt(x)) {
                if (p2 != x) return false;
                x--;
            }
            p2++;
        }
        return true;
    }
